import { CloudUploadOutlined, RedoOutlined } from '@ant-design/icons';
import { Button, Col, Image, Input, Modal, Row, Space, Upload, message } from 'antd';
import React, { useState } from 'react';
import './funtabs.css';
import './index.css';
import { funtabsData } from './linkList';

export default function ChangeWallpaper(props) {
    const {url,setUrl}=props

    const [inputContent, setInputContent] = useState(url)
    const [previewImage, setPreviewImage] = useState(inputContent)
    const [opened, setOpened] = useState(false)
    function openChangeModal() {
        setOpened(true)
    }

    function cancelModal() {
        setOpened(false)
        setInputContent(url)
        setPreviewImage(url)
    }

    function okModal() {
        setOpened(false)
        message.success('壁纸保存成功')
        setUrl(inputContent)
        window.localStorage.setItem('backgroundImage', inputContent)
    }

  return (
    <>
          <Button
              type="primary"
              onClick={openChangeModal}
          >
              更换壁纸
          </Button>
          <Modal
              title='更换壁纸'
              open={opened}
              onCancel={cancelModal}
              footer={null}
              bodyStyle={{
                  display: 'flex',
                  flexDirection: 'column',
              }}
              destroyOnClose
          >
              <Row style={{ marginBottom: '12px', alignItems: 'baseline' }}>
                  <Col flex='78px'>
                      图片地址：
                  </Col>
                  <Col flex='auto'>
                      <Input
                          placeholder='在此输入图片的url地址'
                          value={inputContent}
                          style={{ width: '100%' }}
                          onBlur={
                              (e) => {
                                  setPreviewImage(e.target.value)
                              }
                          }
                          onChange={
                              (e) => {
                                  setInputContent(e.target.value)
                              }
                          }
                      />
                  </Col>
                  <Col>
                      <Upload
                          id='uploadBackgroundImage'
                          accept='.png , .jpg , .jpeg'
                          maxCount={1}
                          beforeUpload={
                              (file) => {
                                  var reader = new FileReader();
                                  reader.readAsDataURL(file);
                                  reader.onloadend = function () {
                                      setInputContent(reader.result)
                                      setPreviewImage(reader.result)
                                  }
                                  return false
                              }
                          }
                          showUploadList={false}
                      >
                          <Button
                              style={{ width: '46px' }}
                              icon={<CloudUploadOutlined />}
                              onClick={() => {
                                  message.warning('上传图片过大会造成加载缓慢,甚至无法加载的情况')
                              }}
                          />
                      </Upload>
                      <Button
                          danger
                          onClick={
                              () => {
                                  setInputContent(funtabsData.backgroundImage)
                                  setPreviewImage(funtabsData.backgroundImage)
                              }
                          }>
                          <RedoOutlined />
                      </Button>
                  </Col>
              </Row>
              <Row style={{ marginBottom: '12px', alignItems: 'stretch' }}>
                  <Col flex='78px'>
                      预览图片：
                  </Col>
                  <Col flex='auto'>
                      <Image
                          src={previewImage}
                          height={150}
                          style={{
                              borderRadius: '6px',
                              minWidth: 150,
                              width:'auto'
                          }}
                          preview={{
                              mask: '预览大图',
                              maskClassName: 'maskStyle',
                              getContainer: (() => document.body)
                          }}
                          fallback="data:image/png;base64,"
                      />
                  </Col>
              </Row>
              <Space style={{ justifyContent: 'flex-end' }}>
                  <Button type='default' onClick={cancelModal}>取消</Button>
                  <Button type='primary' onClick={okModal}>保存</Button>
              </Space>
          </Modal>
    </>
  )
}
